mapper.select:
select t1.msg_id as msgId,t1.msg_pid as msgPid,t1.msg_content as msgContent, t1.create_time as createTime,t1.c_id as cId,t1.oper_role as operRole,t1.oper_id as operId, CASE t1.oper_role WHEN 2 THEN t2.nick_name WHEN 1 THEN t3.s_name END as operName, CASE t1.oper_role WHEN 2 THEN '教师' WHEN 1 THEN '学生' END as operRoleName from t_message t1 LEFT JOIN t_user t2 on t1.oper_id=t2.user_id LEFT JOIN t_student t3 on t1.oper_id=t3.s_id
and t1.c_id=#{cId}
and t1.msg_pid=#{msgPid}
order by t1.create_time desc
